sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		} else {
			return false;
		}
	},
	isDateToday: function(strdate) {
		var today = new GlideDateTime();
		today = today.getLocalDate();

		 // Create a GlideDateTime object for the When needed date
		var whenNeeded = new GlideDateTime(strdate);
		var dateNeeded = whenNeeded.getLocalDate();

		if( dateNeeded.equals(today) ) {
			return true;
		} else {
			return false;
		}

		 //if(!(dateNeeded.after(today))) {
		 //current.setAbortAction(true);
		 //gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.  Please enter a date of time after now!");
	},
    type: 'NeedItUtils'
};
